			{
				return;
			}

			this._items.splice(index, 1);
			if(this._hasLayout)
			{
				item.clearLayout();
				item.setContainer(null);
			}

			var callback = BX.prop.getFunction(this._settings, "onDelete");
			if(callback)
			{
				callback(this, item.getEntity());
			}

